import React, { Component, Fragment } from 'react'

export default class extends Component {
  state = {
    track: {}
  }

  componentDidMount() {
    const { id } = this.props.match.params

    fetch(`https://itunes.apple.com/lookup?id=${id}`)
      .then(res => res.json())
      .then(json => this.setState({ track: json.results[0] }))
  }

  goBack = () => {
    this.props.history.goBack()
  }

  render() {
    const {
      trackName,
      artistName,
      previewUrl,
      track,
    } = this.state.track

    return (
      <Fragment>
        <h2>{trackName}</h2>
        <Media left className="mr-3">
          <Media object src={track.artworkUrl100} alt={track.trackName} />
        </Media>
        <hr />
        Artista: {artistName} <br/>
        <audio controls>
          <source src={previewUrl} type="audio/x-m4a" />
        </audio><br/>
        <button onClick={this.goBack}>Volver</button>
      </Fragment>
    )
  }
}
